script:
frappe.ui.form.on('Sales Order', {
	onload(frm) {
		// your code here
		var amount=new URLSearchParams(window.location.search).get('amount')
		var date=new URLSearchParams(window.location.search).get('transaction_date')
		console.log('AMOUNT='+amount)
		frm.doc.transaction_date=date
		frm.clear_table('items');
		let entry = frm.add_child("items");
        entry.item_code = 'Software Services';
        entry.qty=1
        entry.rate=amount
        entry.amount=amount


	}
})
